package com.cloud.jdk17.controller;import com.cloud.jdk17.SwitchTest;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.PathVariable;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;@RestController@RequestMapping("/jdk")public class JdkController {    @GetMapping("/test/{str}")    public String switchTest(@PathVariable Object str){        return str.toString();    }}