package com.cloud.mybatis.controller;import com.cloud.mybatis.entity.DimDateInfo;import com.cloud.mybatis.service.IDimDateInfoService;import jakarta.annotation.Resource;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.PathVariable;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;@RestController@RequestMapping("/test")public class TestController {    @Resource    private IDimDateInfoService dimDateInfoService;    @GetMapping("/list/{date}")    public DimDateInfo list(@PathVariable String date){        return dimDateInfoService.getByDate(date);    }}