package com.cloud.jdk17;import org.junit.jupiter.api.Test;import static org.junit.jupiter.api.Assertions.*;class SwitchTestTest {    @Test    void JDK12_switch() {        SwitchTest.JDK12_switch("MONDAY");    }    @Test    void JDK13_switch() {        int monday = SwitchTest.JDK13_switch("MONDAY");        assertEquals(1, monday);    }    @Test    void JDK17_instanceof_switch() {        Object o = 123;        String monday = SwitchTest.JDK17_instanceof_switch(o);        assertEquals("Integer:" + o, monday);    }}