package com.cloud.mybatis.controller;import com.baomidou.mybatisplus.core.conditions.query.QueryWrapper;import com.baomidou.mybatisplus.extension.plugins.pagination.Page;import com.cloud.mybatis.entity.DimDateInfo;import com.cloud.mybatis.mapper.DimDateInfoMapper;import org.junit.jupiter.api.Test;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.boot.test.context.SpringBootTest;import java.util.List;import static org.junit.jupiter.api.Assertions.*;@SpringBootTestclass DimDateInfoControllerTest {    @Autowired    private DimDateInfoMapper mapper;    @Test    void testSelect() {        System.out.println(("----- select method test ------"));        QueryWrapper<DimDateInfo> wrapper = new QueryWrapper<>();        wrapper.eq("date", "2022-12-16");        List<DimDateInfo> userList = mapper.selectList(wrapper);        assertEquals(1, userList.size());        userList.forEach(System.out::println);    }    @Test     void testPageSelect() {        QueryWrapper<DimDateInfo> wrapper = new QueryWrapper<>();        wrapper.eq("year", 2022);        Page<DimDateInfo> page = new Page<>(1, 10);        List<DimDateInfo> userList = mapper.selectPage(page, wrapper).getRecords();        assertEquals(10, userList.size());        userList.forEach(System.out::println);    }}