package com.cloud.redis;import com.cloud.redis.util.RedisUtils;import jakarta.annotation.Resource;import org.junit.jupiter.api.Assertions;import org.junit.jupiter.api.Test;import org.springframework.boot.test.context.SpringBootTest;import static java.lang.System.out;/** * @Author zhouTao * @Date 2023/2/10 */@SpringBootTestclass RedisUtilsTest {    @Resource    private RedisUtils redisUtils;    @Test    void get() {        Object hello = redisUtils.get("hello");        out.println(hello);        Assertions.assertEquals("world", hello);    }    @Test    void getPv() {        Object pv = redisUtils.get("hello");        out.println(pv);        Assertions.assertEquals("world", pv);    }    @Test    void getUv() {        redisUtils.set("uv:01", "T");        Object pv = redisUtils.get("uv:01");        out.println(pv);        Assertions.assertEquals("T", pv);    }    @Test    void getlGet() {        long strList = redisUtils.lGetListSize("str_list");        out.println(strList);        Object pv = redisUtils.lGet("str_list", 0, 1);        Assertions.assertEquals("T", pv);    }    @Test    void bitGs() {        String key = "bitTest";        Boolean bitTest = redisUtils.setBit(key, 10, true);        out.println("offset 10: " + bitTest);        Boolean bitTest1 = redisUtils.getBit(key, 10);        out.println("offset 10: " + bitTest1);        Long l = redisUtils.bitCount(key);        out.println(l);        Long l1 = redisUtils.bitTop(key);        out.println(l1);    }}