package com.cloud.redis;import com.cloud.redis.util.RedisUtils;import jakarta.annotation.Resource;import org.junit.jupiter.api.Assertions;import org.junit.jupiter.api.Test;import org.springframework.boot.test.context.SpringBootTest;/** * @Author zhouTao * @Date 2023/2/10 */@SpringBootTestclass RedisUtilsTest {    @Resource    private RedisUtils redisUtils;    @Test    void get() {        Object hello = redisUtils.get("hello");        Assertions.assertEquals("world", hello);    }    @Test    void getPv() {        Object pv = redisUtils.get("pv");        System.out.println(pv);        Assertions.assertEquals("world", pv);    }    @Test    void getUv() {        Object pv = redisUtils.get("uv:1");        System.out.println(pv);        Assertions.assertEquals("world", pv);    }}