package com.cloud.test.controller;import com.baomidou.mybatisplus.core.conditions.query.QueryWrapper;import com.cloud.test.entity.DimDateInfo;import com.cloud.test.mapper.DimDateInfoMapper;import org.junit.jupiter.api.Test;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.boot.test.context.SpringBootTest;import java.util.List;import static org.junit.jupiter.api.Assertions.*;@SpringBootTestclass DimDateInfoControllerTest {    @Autowired    private DimDateInfoMapper mapper;    @Test    public void testSelect() {        System.out.println(("----- select method test ------"));        QueryWrapper<DimDateInfo> wrapper = new QueryWrapper<>();        wrapper.eq("date", "2022-12-16");        List<DimDateInfo> userList = mapper.selectList(wrapper);        assertEquals(1, userList.size());        userList.forEach(System.out::println);    }}